#jj=aa^bb mod oo
def exp(aa, bb, oo)

  ii=oo
  j=0
  jj=0
  kk=[2**12] #prime is 4096 bit table
  c=[2**13]  #mod is 8192 bit table
  count=0

  for i in 0..4096
    kk[i]=0
  end
  while(ii>0)
	ii = (ii>>1)
	j=j+1
  end


  kk[0]=aa
  cc=bb
#  print j,"\n"
  
#ex.1000=2**3+2**5+2**6+2**7+2**8+2**9 makes a array c=[3,5,6,7,8,9]
    for i in 0..j
      if ((cc^1)%2 == 0) # testbit(bb,i)
	c[count]=i
	count=count+1
      end
      cc=(cc>>1)
    end
#    print count,"\n"

    for i in 1..(c[count-1]+1)
      kk[i] = kk[i-1]*kk[i-1]%oo
    end

    jj=1
    for i in 0..count-1
      jj=kk[c[i]]*jj%oo
      if (jj==0)
#	print i,"\n"
      end
    end

    return jj
end


