def elgamal(mm, hh, mod)

#choose random number r in N
  r=888888

  cc=exp(GROUP_GENERATOR,r,mod)
  dd=exp(hh,r,mod)*mm
  @Cyp_cc=cc
  @Cyp_dd=dd
  print "(",cc,"," , dd , ")" ,"\n"
#  return cyp

end

#cyphertext to plaintext
def decode(cc, dd, x, mod)

  gcd=0
  plain=dd*inv(exp(cc,x,mod),mod,gcd)%mod

  return plain

end

